(function() {
  module.exports = function(dbName) {
    var db;
    if (dbName == null) {
      dbName = 'fs';
    }
    db = new Dexie(dbName);
    db.version(1).stores({
      files: 'path, blob, type, createdAt, updatedAt'
    });
    return db;
  };

}).call(this);

dynamo_stuff.content:
(function() {
  var pinvoke, queryDynamoDB, readFromDynamoDB, writeToDynamoDB;

  pinvoke = require("./util").pinvoke;

  writeToDynamoDB = function(table, id, path, sha) {
    var params, time;
    time = "" + (+(new Date));
    params = {
      Item: {
        owner: {
          S: id
        },
        path: {
          S: path
        },
        created_at: {
          S: time
        },
        sha: {
          S: sha
        }
      }
    };
    return pinvoke(table, "putItem", params);
  };

  queryDynamoDB = function(table, id) {
    var params;
    table = table;
    params = {
      AttributesToGet: ["path", "sha"],
      KeyConditions: {
        owner: {
          ComparisonOperator: "EQ",
          AttributeValueList: [
            {
              S: id
            }
          ]
        }
      }
    };
    return pinvoke(table, "query", params);
  };

  readFromDynamoDB = function(table, id, path) {
    var params;
    params = {
      Key: {
        owner: {
          S: id
        },
        path: {
          S: path
        }
      }
    };
    return pinvoke(table, "getItem", params);
  };

}).call(this);

experiments/dexie-fs.content:
(function() {
  var DexieFS, Filesystem, fs;

  DexieFS = require("../fs/dexie-driver");

  Filesystem = require("../fs/filesystem");

  fs = Filesystem(DexieFS('fs'));

  fs.cd("goose");

  fs.cd("rad");

  fs.cd("/grass");

  fs.ls("/").then(function(results) {
    return console.log(results);
  });

  fs.write("test", new Blob(['duder'])).then(function() {
    return fs.read("test").then(function(file) {
      return console.log(file);
    })["catch"](function(e) {
      return console.error(e);
    });
  });

}).call(this);

fs/dexie-driver.content:
(function() {
  var uniq;

  uniq = require("../util").uniq;

  module.exports = function(db) {
    var Files;
    Files = db.files;
    return {
      read: function(path) {
        return Files.get(path);
      },
      write: function(path, blob) {
        return Files.put({
          path: path,
          blob: blob,
          type: blob.type,
          updatedAt: +(new Date)
        });
      },
      "delete": function(path) {
        return Files["delete"](path);
      },
      list: function(dir) {
        return Files.where("path").startsWith(dir).toArray().then(function(results) {
          return uniq(results.map(function(_arg) {
            var path;
            path = _arg.path;
            return path = path.replace(dir, "").replace(/\/.*$/, "/");
          }));
        });
      }
    };
  };

}).call(this);

fs/filesystem.content:
(function() {
  var __slice = [].slice;

  module.exports = function(driver) {
    var SEPARATOR, join, normalizeDir, normalizePath, parsePath, pwd;
    SEPARATOR = "/";
    pwd = SEPARATOR;
    normalizeDir = function(dir) {
      return normalizePath(dir).replace(/\/?$/, SEPARATOR);
    };
    normalizePath = function(path) {
      return path.replace(/\/\/+/, SEPARATOR).replace(/\/[^/]*\/\.\./g, "").replace(/\/\.\//g, SEPARATOR);
    };
    join = function() {
      var paths;
      paths = 1 <= arguments.length ? __slice.call(arguments, 0) : [];
      return normalizePath(paths.join(SEPARATOR));
    };
    parsePath = function(path) {
      if (path.indexOf(SEPARATOR) === 0) {
        return normalizePath(path);
      } else {
        return join(pwd, path);
      }
    };
    return {
      read: function(path) {
        path = parsePath(path);
        return driver.read(path);
      },
      write: function(path, blob) {
        path = parsePath(path);
        return driver.write(path, blob);
      },
      ls: function(dir) {
        if (dir == null) {
          dir = pwd;
        }
        dir = normalizeDir(parsePath(dir));
        return driver.list(dir);
      },
      rm: function(path) {
        path = parsePath(path);
        return driver["delete"](path);
      },
      cd: function(path) {
        return pwd = normalizeDir(parsePath(path));
      },
      pwd: function() {
        return pwd;
      }
    };
  };

}).call(this);

fs/ram-driver.content:
(function() {
  var P, startsWith, uniq, _ref;

  module.exports = function(files) {
    var notFound, self;
    if (files == null) {
      files = [];
    }
    notFound = "File not found";
    return self = {
      read: function(path) {
        return P(function() {
          var file;
          file = files.filter(function(_arg) {
            var filePath;
            filePath = _arg.path;
            return path === filePath;
          })[0];
          if (file) {
            return file;
          } else {
            throw notFound;
          }
        });
      },
      write: function(path, blob) {
        return self.read(path)["catch"](function(error) {
          if (error === notFound) {

          } else {
            throw error;
          }
        }).then(function(file) {
          var now;
          now = +(new Date);
          if (file) {
            file.blob = blob;
            file.type = blob.type;
            return file.updatedAt = now;
          } else {
            return files.push({
              path: path,
              blob: blob,
              type: blob.type,
              updatedAt: now,
              createdAt: now
            });
          }
        });
      },
      "delete": function(path) {
        return P(function() {
          files = files.filter(function(_arg) {
            var filePath;
            filePath = _arg.path;
            return path !== filePath;
          });
        });
      },
      list: function(dir) {
        return P(function() {
          return files.filter(function(_arg) {
            var path;
            path = _arg.path;
            return startsWith(path, dir);
          });
        }).then(function(results) {
          return uniq(results.map(function(_arg) {
            var path;
            path = _arg.path;
            return path = path.replace(dir, "").replace(/\/.*$/, "/");
          }));
        });
      }
    };
  };

  P = function(fn) {
    return Promise.resolve().then(fn);
  };

  _ref = require("../util"), startsWith = _ref.startsWith, uniq = _ref.uniq;

}).call(this);

fs/s3-driver.content:
(function() {
  var blob, deleteFromS3, delimiter, endsWith, getFromS3, json, list, log, pinvoke, startsWith, status, uploadToS3, _ref;

  _ref = require("../util"), log = _ref.log, pinvoke = _ref.pinvoke, startsWith = _ref.startsWith, endsWith = _ref.endsWith;

  delimiter = "/";

  status = function(response) {
    if (response.status >= 200 && response.status < 300) {
      return response;
    } else {
      throw response;
    }
  };

  json = function(response) {
    return response.json();
  };

  blob = function(response) {
    return response.blob();
  };

  uploadToS3 = function(bucket, key, file, options) {
    var cacheControl;
    if (options == null) {
      options = {};
    }
    cacheControl = options.cacheControl;
    if (cacheControl == null) {
      cacheControl = 0;
    }
    return pinvoke(bucket, "putObject", {
      Key: key,
      ContentType: file.type,
      Body: file,
      CacheControl: "max-age=" + cacheControl
    });
  };

  getFromS3 = function(bucket, key) {
    return fetch("https://" + bucket.config.params.Bucket + ".s3.amazonaws.com/" + key).then(status).then(blob);
  };

  deleteFromS3 = function(bucket, key) {
    return pinvoke(bucket, "deleteObject", {
      Key: key
    });
  };

  list = function(bucket, id, dir) {
    var prefix;
    if (!startsWith(dir, delimiter)) {
      dir = "" + delimiter + dir;
    }
    if (!endsWith(dir, delimiter)) {
      dir = "" + dir + delimiter;
    }
    prefix = "" + id + dir;
    return pinvoke(bucket, "listObjects", {
      Prefix: prefix,
      Delimiter: delimiter
    }).then(function(result) {
      return result.CommonPrefixes.map(function(p) {
        return p.Prefix.replace(prefix, "");
      }).concat(result.Contents.map(function(o) {
        return o.Key.replace(prefix, "");
      }));
    });
  };

  module.exports = function(id, bucket) {
    return {
      read: function(path) {
        var key;
        if (!startsWith(path, delimiter)) {
          path = delimiter + path;
        }
        key = "" + id + path;
        return getFromS3(bucket, key).then(function(blob) {
          return {
            path: path,
            blob: blob
          };
        });
      },
      write: function(path, file) {
        var key;
        if (!startsWith(path, delimiter)) {
          path = delimiter + path;
        }
        key = "" + id + path;
        return uploadToS3(bucket, key, file);
      },
      "delete": function(path) {
        var key;
        if (!startsWith(path, delimiter)) {
          path = delimiter + path;
        }
        key = "" + id + path;
        return deleteFromS3(bucket, key);
      },
      list: function(dir) {
        if (dir == null) {
          dir = "/";
        }
        return list(bucket, id, dir);
      }
    };
  };

}).call(this);

