lib/sha.content:
(function() {
  var arrayBufferToBase64, demo, shaForBlob, urlSafeBase64;

  urlSafeBase64 = function(base64) {
    return base64.replace(/\+/g, "-").replace(/\//g, "_").replace(/\=*$/, '');
  };

  arrayBufferToBase64 = function(buffer) {
    var binary, bytes;
    bytes = new Uint8Array(buffer);
    binary = "";
    bytes.forEach(function(byte) {
      return binary += String.fromCharCode(byte);
    });
    return btoa(binary);
  };

  shaForBlob = function(blob) {
    return new Promise(function(resolve, reject) {
      var reader;
      reader = new FileReader;
      reader.onload = function() {
        var buffer;
        buffer = reader.result;
        return crypto.subtle.digest("SHA-256", buffer).then(resolve)["catch"](reject);
      };
      reader.onerror = reject;
      return reader.readAsArrayBuffer(blob);
    });
  };

  demo = function() {
    return ["yolo", "duder", "hello", "wat"].forEach(function(test) {
      var blob;
      blob = new Blob([test]);
      return shaForBlob(blob).then(arrayBufferToBase64).then(urlSafeBase64).then(log);
    });
  };

  module.exports = function(blob) {
    return shaForBlob(blob).then(arrayBufferToBase64).then(urlSafeBase64);
  };

}).call(this);

