(function() {
  var Drop, Filesystem, LocalDriver, OS, OSTemplate, S3Driver, awsLogin, db, emptyElement, initFileDrop, log, loginTemplate, logins, os, pinvoke, receivedCredentials, style, _ref;

  require("./lib/directory_upload");

  style = document.createElement("style");

  style.innerHTML = require("./style");

  document.head.appendChild(style);

  _ref = require("./util"), log = _ref.log, emptyElement = _ref.emptyElement, pinvoke = _ref.pinvoke;

  Drop = require("./lib/drop");

  Filesystem = require("./fs/filesystem");

  S3Driver = require("./fs/s3-driver");

  LocalDriver = require("./fs/dexie-driver");

  OS = require("./os");

  os = OS();

  global.os = os;

  OSTemplate = require("./templates/os");

  document.body.appendChild(OSTemplate(os));

  initFileDrop = function(element, processItem) {
    return Drop(element, function(e) {
      var handleFiles;
      handleFiles = function(items, path) {
        if (path == null) {
          path = "/";
        }
        return items.forEach(function(item) {
          if (item.getFilesAndDirectories) {
            return item.getFilesAndDirectories().then(function(items) {
              return handleFiles(items, item.path);
            });
          } else {
            return processItem(item, path);
          }
        });
      };
      return e.dataTransfer.getFilesAndDirectories().then(function(items) {
        return handleFiles(items);
      });
    });
  };

  initFileDrop(document, function(file, path) {
    var key;
    key = ("" + path + "/" + file.name).replace(/\/+/, "/");
    return os.put(key, file);
  });

  AWS.config.update({
    region: 'us-east-1'
  });

  if (false) {
    try {
      logins = JSON.parse(localStorage.WHIMSY_FS_AWS_LOGIN);
    } catch (_error) {}
    AWS.config.credentials = new AWS.CognitoIdentityCredentials({
      IdentityPoolId: 'us-east-1:4fe22da5-bb5e-4a78-a260-74ae0a140bf9',
      Logins: logins
    });
    if (logins) {
      pinvoke(AWS.config.credentials, "get").then(receivedCredentials)["catch"](function(e) {
        return console.error(e);
      });
    }
    awsLogin = require("./amazon_login").awsLogin;
    loginTemplate = require("./templates/login")({
      click: function() {
        var options;
        options = {
          scope: 'profile'
        };
        return awsLogin(options).then(function(logins) {
          localStorage.WHIMSY_FS_AWS_LOGIN = JSON.stringify(logins);
          return receivedCredentials();
        });
      }
    });
    document.body.appendChild(loginTemplate);
    receivedCredentials = function() {
      var bucket, id;
      console.log(AWS.config.credentials);
      id = AWS.config.credentials.identityId;
      document.body.removeChild(loginTemplate);
      bucket = new AWS.S3({
        params: {
          Bucket: "whimsy-fs"
        }
      });
      return os.attachFS(Filesystem(S3Driver(id, bucket)));
    };
  } else {
    db = require("./db")('fs');
    os.attachFS(Filesystem(LocalDriver(db)));
    global.db = db;
  }

}).call(this);

os.content:
(function() {
  var EditorTemplate, FolderPresenter, FolderTemplate, LauncherElement, Observable, PixelEditor, TextEditor, readAsText;

  Observable = require("observable");

  TextEditor = require("./text_editor");

  PixelEditor = require("./pixel_editor");

  EditorTemplate = require("./templates/editor");

  FolderTemplate = require("./templates/folder");

  FolderPresenter = require("./presenters/folder");

  LauncherElement = require("./templates/launcher");

  readAsText = require("../util").readAsText;

  module.exports = function() {
    var appHandlers, self;
    appHandlers = {
      "^text": function(file, path) {
        var editor;
        editor = TextEditor(self);
        readAsText(file).then(function(contents) {
          editor.contents(contents);
          editor.contentType(file.type);
          return editor.path(path);
        });
        return EditorTemplate(editor);
      },
      "^image": function(file, path) {
        return PixelEditor(self, file, path);
      }
    };
    self = {
      editorElement: Observable(),
      fileTreeElement: Observable(),
      attachFS: function(fs) {
        self.fileTreeElement(FolderTemplate(FolderPresenter({
          path: "/"
        }, self)));
        return self.fs = function() {
          return fs;
        };
      },
      open: function(path) {
        return self.fs().read(path).then(function(_arg) {
          var blob, handled, type;
          blob = _arg.blob;
          type = blob.type;
          console.log("Open: ", path, blob.type);
          handled = false;
          return Object.keys(appHandlers).forEach(function(matcher) {
            var appElement, handler, regex;
            if (handled) {
              return;
            }
            handler = appHandlers[matcher];
            regex = new RegExp(matcher);
            if (regex.test(type)) {
              handled = true;
              appElement = handler(blob, path);
              return self.editorElement(appElement);
            }
          });
        });
      },
      list: function(path) {
        return self.fs().ls(path);
      },
      put: function(path, file) {
        return self.fs().write(path, file);
      },
      "delete": function(path) {
        return self.fs()["delete"](path);
      },
      apps: [
        {
          name: "Pixel",
          launch: function() {
            return self.editorElement(PixelEditor(self));
          }
        }
      ]
    };
    self.launcherElement = LauncherElement(self);
    return self;
  };

}).call(this);

