(function() {
  var FilePresenter, FileTemplate, FolderPresenter, FolderTemplate, Observable, endsWith;

  Observable = require("observable");

  FolderTemplate = require("../templates/folder");

  FileTemplate = require("../templates/file");

  FilePresenter = require("./file");

  endsWith = require("../util").endsWith;

  module.exports = FolderPresenter = function(data, os) {
    var files, folders, name, path, self;
    path = data.path, folders = data.folders, files = data.files, name = data.name;
    if (name == null) {
      name = path;
    }
    if (folders == null) {
      folders = [];
    }
    if (files == null) {
      files = [];
    }
    return self = {
      Folder: function(data) {
        return FolderTemplate(FolderPresenter(data, os));
      },
      File: function(data) {
        return FileTemplate(FilePresenter(data, os, self));
      },
      "class": function() {
        if (self.expanded()) {
          return "expanded";
        }
      },
      click: function(e) {
        if (e.filetreeHandled) {
          return;
        }
        e.filetreeHandled = true;
        self.expanded.toggle();
        if (self.expanded()) {
          self.refresh();
        }
        return false;
      },
      expanded: Observable(false),
      folders: Observable(folders),
      files: Observable(files),
      name: name,
      path: path,
      refresh: function() {
        console.log("List:", path);
        return os.list(path).then(function(results) {
          files = results.filter(function(result) {
            return !endsWith(result, "/");
          });
          folders = results.filter(function(result) {
            return endsWith(result, "/");
          });
          self.files(files);
          return self.folders(folders);
        });
      }
    };
  };

}).call(this);

style.content:
module.exports = "* {
  box-sizing: border-box;
}
body {
  font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;
  font-weight: 300;
  margin: 0;
}
body,
html {
  height: 100%;
}
#LoginWithAmazon {
  position: absolute;
  top: 0;
}
file {
  padding-left: 1em;
  position: relative;
}
file .path {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
file > .delete {
  display: none;
  position: absolute;
  right: 1em;
  top: 0;
}
file:hover {
  background-color: #ffa;
}
file:hover > .delete {
  display: inline-block;
}
folder {
  display: block;
  padding-left: 1em;
}
folder::before {
  content: "[+]";
}
folder > file,
folder > folder {
  display: none;
}
folder.expanded::before {
  content: "[-]";
}
folder.expanded > file,
folder.expanded > folder {
  display: block;
}
.os {
  display: flex;
  height: 100%;
}
.os > folder {
  flex-grow: 0;
  padding-left: 0;
  width: 250px;
}
.os > iframe {
  flex-grow: 1;
  height: 100%;
}
editor {
  display: block;
  margin-top: 1em;
}
textarea {
  display: block;
  width: 100%;
}
launcher {
  display: block;
  position: absolute;
  bottom: 0;
}
";

