(function() {
  var DB, DexieDriver, Filesystem, readAsText;

  DexieDriver = require("../fs/dexie-driver");

  Filesystem = require("../fs/filesystem");

  DB = require("../db");

  readAsText = require("../util").readAsText;

  describe("Dexie FS", function() {
    var db, fs;
    db = DB('fs');
    fs = Filesystem(DexieDriver(db));
    it("should write, read, and delete files", function(done) {
      var blob;
      blob = new Blob(["yolo"]);
      return fs.write("test", blob).then(function() {
        return fs.read("test");
      }).then(function(_arg) {
        var blob;
        blob = _arg.blob;
        return readAsText(blob);
      }).then(function(content) {
        return assert.equal(content, "yolo");
      }).then(function() {
        return done();
      })["catch"](done);
    });
    return it("should list files in the directory", function(done) {
      var blob;
      blob = new Blob(["yolo"]);
      return Promise.all(["test2", "yolo/yo", "yolo/yo2"].map(function(path) {
        return fs.write(path, blob);
      })).then(function() {
        return fs.ls("yolo");
      }).then(function(results) {
        assert.equal(results.length, 2);
        return done();
      })["catch"](done);
    });
  });

}).call(this);

