(function() {
  var Postmaster, initWindow, scriptContent;

  Postmaster = require("../main");

  scriptContent = function() {
    var fn;
    fn = function() {
      var pm;
      pm = Postmaster();
      pm.echo = function(value) {
        return value;
      };
      pm.throws = function() {
        throw new Error("This always throws");
      };
      return pm.promiseFail = function() {
        return Promise.reject(new Error("This is a failed promise"));
      };
    };
    return "var module = {};\nPostmaster = " + PACKAGE.distribution.main.content + ";\n(" + (fn.toString()) + ")();";
  };

  initWindow = function(targetWindow) {
    return targetWindow.document.write("<script>" + (scriptContent()) + "<\/script>");
  };

  describe("Postmaster", function() {
    it("should work with openened windows", function(done) {
      var childWindow, postmaster;
      childWindow = open("", null, "width=200,height=200");
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("echo", 5).then(function(result) {
        return assert.equal(result, 5);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return childWindow.close();
      });
    });
    it("should work with iframes", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("echo", 17).then(function(result) {
        return assert.equal(result, 17);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should handle the remote call throwing errors", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("throws")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should throwing a useful error when the remote doesn't define the function", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("someUndefinedFunction")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should handle the remote call returning failed promises", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("promiseFail")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should be able to go around the world", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      postmaster.yolo = function(txt) {
        return "heyy " + txt;
      };
      return postmaster.invokeRemote("invokeRemote", "yolo", "cool").then(function(result) {
        return assert.equal(result, "heyy cool");
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should work with web workers", function(done) {
      var base, blob, jsUrl, postmaster, worker;
      blob = new Blob([scriptContent()]);
      jsUrl = URL.createObjectURL(blob);
      worker = new Worker(jsUrl);
      base = {
        remoteTarget: function() {
          return worker;
        },
        receiver: function() {
          return worker;
        }
      };
      postmaster = Postmaster(base);
      return postmaster.invokeRemote("echo", 17).then(function(result) {
        return assert.equal(result, 17);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return worker.terminate();
      });
    });
    return it("should fail quickly when contacting a window that doesn't support Postmaster", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("echo", 5)["catch"](function(e) {
        if (e.message.match(/no ack/i)) {
          return done();
        } else {
          return done(1);
        }
      }).then(function() {
        return iframe.remove();
      });
    });
  });

}).call(this);
